// news-cms-module/middlewares/multerMiddleware.js
const multer = require('multer');

// --- PERUBAHAN DI SINI ---
// Ganti diskStorage menjadi memoryStorage.
// Ini akan menyimpan file di memori sementara (req.file.buffer),
// sehingga tidak ada error 'mkdir' atau permission di Vercel.
const storage = multer.memoryStorage(); 

const upload = multer({ 
    storage: storage,
    // Gunakan filter file yang sudah Anda buat sebelumnya
    fileFilter: (req, file, cb) => {
        if (file.mimetype == "image/png" || file.mimetype == "image/jpg" || file.mimetype == "image/jpeg") {
            cb(null, true);
        } else {
            cb(null, false);
            return cb(new Error('Only .png, .jpg and .jpeg format allowed!'));
        }
    },
    limits: { fileSize: 5 * 1024 * 1024 } // Optional: limit 5MB
});

module.exports = upload;